import './Task1.css'

function Button(props) {
    return (
        <button className='button' style={{ backgroundColor: props.bgColor }}>{props.colorText}</button>
    );
}


function Block(props){
    return(
        <div style={{backgroundColor:props.bgColor}}>
            Если бы ежики были слонами, мыши были бы котами
        </div>
    );
}

function Task1() {
    return (
        <div className='flex-col'>
            <div className='flex'>
                <Button bgColor="red" colorText="Red" />
                <Button bgColor="green" colorText="Green" />
                <Button bgColor="blue" colorText="Blue" />
            </div>
            <Block b/>
        </div>
    );
}


export default Task1;
